mapper.select:
select * from music_info
where mu_name like '%${keyword}%'
limit 10 offset #{offset}

mapper.update:
UPDATE 
music_info
SET 
mu_name=#{mu_name}, mu_ai_seq=#{mu_ai_seq}, mu_lyrics=#{mu_lyrics}, mu_ab_seq=#{mu_ab_seq}, mu_img_file=#{mu_img_file}, mu_music_file=#{mu_music_file},
mu_playtime=#{mu_playtime}, mu_explain_txt=#{mu_explain_txt}, mu_age=#{mu_age} 
WHERE mu_seq=#{mu_seq}
